import axios from "axios";
import './Play.css'
import React, { useEffect, useState } from "react";
import {  
    Modal,
    ModalOverlay,
    ModalContent,
    ModalHeader,
    ModalFooter,
    ModalBody,
    ModalCloseButton,
    useDisclosure
} from "@chakra-ui/react";

const Play = () => { 
    const USERS_REST_API_URL = 'http://localhost:8080/api/users'
    const [amount, setAmount] = useState(0)
    const [uni, setUni] = useState(true)
    const [scholarship, setScholarship] = useState(0)
    const [level, setLevel] = useState(0)
    const [prompts, setPrompts] = useState([])
    const { isOpen, onOpen, onClose } = useDisclosure();

    useEffect(() => {
        axios.get(USERS_REST_API_URL)
        .then(res => { 
            console.log(res.data.length)
            console.log(res.data[0].firstName)
            for (var i = 0; i < res.data.length; i++) {
                setPrompts(prompts => [...prompts,
                    {name: res.data[i].firstName}
                ])

            }
        });
    }, [])

    useEffect(() => {
        onOpen();
    }, [onOpen]);    

    return (

        <div className="Play">
            <Modal isOpen={isOpen} onClose={onClose} isCentered>
        <ModalOverlay />
        <ModalContent>
          <ModalHeader>
            Hello! Welcome to The Shoppies{" "}
            <span role="img" aria-label="wave">
              👋
            </span>
          </ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <Body1>
              Thank you for checking out my submission for the Shopify UX
              Developer &amp; Web Developer Intern challenge!
            </Body1>
            <br />
            <Body1>
              Both email <b>authentication</b> and saving nominations to a{" "}
              <b>database</b> are implemented. Feel free to sign up (any unique
              email will work) or log in using a{" "}
              <Link fontSize="16px" onClick={usePreMadeAccount}>
                pre-made account
              </Link>
              .
            </Body1>
            <Body1>
              <br />I encourage signing up since the pre-made account is
              publicly accessible for demonstration purposes, so it may not have
              a fresh experience.
            </Body1>
            <br />
            <Body1>
              Thank you,
              <br />
              Tommy Deng
            </Body1>
          </ModalBody>
          <ModalFooter>
            <Stack direction="column" justify="end" width="100%">
              <ButtonSecondary paddingY="16px" onClick={usePreMadeAccount}>
                Use pre-made account
              </ButtonSecondary>
              <ButtonPrimary onClick={onClose}>Close</ButtonPrimary>
            </Stack>
          </ModalFooter>
        </ModalContent>
      </Modal>

        <div className="profile-section">
        <ul>
          <li>current savings: $50</li><br/>
          <li>level: 1</li>
          {prompts.map(prompt=> (
            <li>{prompt.name}</li>
          ))}
        </ul>
      </div>

    {/* <h2> Coming Soon </h2> */}
    </div>
    );
}

export default Play;
